import "./App.css";
import { Helmet } from "react-helmet";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function App() {
  // create elemnt object whose id is (" -------- ") ------------------//
  const activeToolEl = document.getElementById("active-tool");
  const brushColorBtn = document.getElementById("brush-color");
  const brushIcon = document.getElementById("brush");
  const brushSize = document.getElementById("brush-size");
  const brushSlider = document.getElementById("brush-slider");
  const bucketColorBtn = document.getElementById("bucket-color");
  const eraser = document.getElementById("eraser");
  const clearCanvasBtn = document.getElementById("clear-canvas");
  const saveStorageBtn = document.getElementById("save-storage");
  const loadStorageBtn = document.getElementById("load-storage");
  const clearStorageBtn = document.getElementById("clear-storage");
  const downloadBtn = document.getElementById("download");
  const { body } = document;
  // -----------------------------------------------------------------//

  // -------------- Global Variables ------------------------ //
  const canvas = document.createElement("canvas");
  canvas.id = "canvas";
  const context = canvas.getContext("2d");
  let currentSize = 10;
  let bucketColor = "#FFFFFF";
  let currentColor = "#A51DAB";
  let isEraser = false;
  let isMouseDown = false;
  let drawnArray = [];
  // -------------------------------------------------------//

  // -------------- Formatting Brush Size --------------------//
  function displayBrushSize() {
    // if brushSlider - (document with id = brush-slider) value is less than 10
    if (brushSlider.value < 10) {
      // change the text of brushSIze tp brushSlider value and add zero before it;
      brushSize.textContent = `0${brushSlider.value}`;
    } else {
      // change the text of brushSIze tp brushSlider value
      brushSize.textContent = brushSlider.value;
    }
  }
  // ------------------------------------------------------------//

  // Setting Brush Size
  brushSlider.addEventListener("change", () => {
    currentSize = brushSlider.value;
    displayBrushSize();
  });

  // Setting Brush Color
  brushColorBtn.addEventListener("change", () => {
    isEraser = false;
    currentColor = `#${brushColorBtn.value}`;
  });

  // Setting Background Color
  bucketColorBtn.addEventListener("change", () => {
    bucketColor = `#${bucketColorBtn.value}`;
    createCanvas();
    restoreCanvas();
  });

  // Eraser
  eraser.addEventListener("click", () => {
    isEraser = true;
    brushIcon.style.color = "white";
    eraser.style.color = "black";
    activeToolEl.textContent = "Eraser";
    currentColor = bucketColor;
    currentSize = 50;
  });

  // Switch back to Brush
  function switchToBrush() {
    isEraser = false;
    activeToolEl.textContent = "Brush";
    brushIcon.style.color = "black";
    eraser.style.color = "white";
    currentColor = `#${brushColorBtn.value}`;
    currentSize = 10;
    brushSlider.value = 10;
    displayBrushSize();
  }

  // Create Canvas
  function createCanvas() {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight - 50;
    context.fillStyle = bucketColor;
    context.fillRect(0, 0, canvas.width, canvas.height);
    body.appendChild(canvas);
    switchToBrush();
  }

  // Clear Canvas
  clearCanvasBtn.addEventListener("click", () => {
    createCanvas();
    drawnArray = [];
    // Active Tool
    activeToolEl.textContent = "Canvas Cleared";
    setTimeout(switchToBrush, 1500);
  });

  // Draw what is stored in DrawnArray
  function restoreCanvas() {
    for (let i = 1; i < drawnArray.length; i++) {
      context.beginPath();
      context.moveTo(drawnArray[i - 1].x, drawnArray[i - 1].y);
      context.lineWidth = drawnArray[i].size;
      context.lineCap = "round";
      if (drawnArray[i].eraser) {
        context.strokeStyle = bucketColor;
      } else {
        context.strokeStyle = drawnArray[i].color;
      }
      context.lineTo(drawnArray[i].x, drawnArray[i].y);
      context.stroke();
    }
  }

  // Store Drawn Lines in DrawnArray
  function storeDrawn(x, y, size, color, erase) {
    const line = {
      x,
      y,
      size,
      color,
      erase,
    };
    drawnArray.push(line);
  }

  // Get Mouse Position
  function getMousePosition(event) {
    const boundaries = canvas.getBoundingClientRect();
    return {
      x: event.clientX - boundaries.left,
      y: event.clientY - boundaries.top,
    };
  }

  // Mouse Down
  canvas.addEventListener("mousedown", (event) => {
    isMouseDown = true;
    const currentPosition = getMousePosition(event);
    context.moveTo(currentPosition.x, currentPosition.y);
    context.beginPath();
    context.lineWidth = currentSize;
    context.lineCap = "round";
    context.strokeStyle = currentColor;
  });

  // Mouse Move
  canvas.addEventListener("mousemove", (event) => {
    if (isMouseDown) {
      const currentPosition = getMousePosition(event);
      context.lineTo(currentPosition.x, currentPosition.y);
      context.stroke();
      storeDrawn(
        currentPosition.x,
        currentPosition.y,
        currentSize,
        currentColor,
        isEraser
      );
    } else {
      storeDrawn(undefined);
    }
  });

  // Mouse Up
  canvas.addEventListener("mouseup", () => {
    isMouseDown = false;
  });

  // Save to Local Storage
  saveStorageBtn.addEventListener("click", () => {
    localStorage.setItem("savedCanvas", JSON.stringify(drawnArray));
    // Active Tool
    activeToolEl.textContent = "Canvas Saved";
    setTimeout(switchToBrush, 1500);
  });

  // Load from Local Storage
  loadStorageBtn.addEventListener("click", () => {
    if (localStorage.getItem("savedCanvas")) {
      drawnArray = JSON.parse(localStorage.savedCanvas);
      restoreCanvas();
      // Active Tool
      activeToolEl.textContent = "Canvas Loaded";
      setTimeout(switchToBrush, 1500);
    } else {
      activeToolEl.textContent = "No Canvas Found";
      setTimeout(switchToBrush, 1500);
    }
  });

  // Clear Local Storage
  clearStorageBtn.addEventListener("click", () => {
    localStorage.removeItem("savedCanvas");
    // Active Tool
    activeToolEl.textContent = "Local Storage Cleared";
    setTimeout(switchToBrush, 1500);
  });

  // Download Image
  downloadBtn.addEventListener("click", () => {
    downloadBtn.href = canvas.toDataURL("image/jpeg", 1);
    downloadBtn.download = "paint-example.jpeg";
    // Active Tool
    activeToolEl.textContent = "Image File Saved";
    setTimeout(switchToBrush, 1500);
  });

  // Event Listener
  brushIcon.addEventListener("click", switchToBrush);

  // On Load
  createCanvas();

  return (
    <div>
      <div>
        <div className="top-bar">
          {/* Active Tool */}
          <div className="active-tool">
            <span id="active-tool" title="Active Tool">
              Brush
            </span>
          </div>
          {/* Brush */}
          <div className="brush tool">
            <i className="fas fa-brush" id="brush" title="Brush" />
            <input className="jscolor" defaultValue="a51dab" id="brush-color" />
            <span className="size" id="brush-size" title="Brush Size">
              10
            </span>
            <input
              type="range"
              min={1}
              max={50}
              defaultValue={10}
              className="slider"
              id="brush-slider"
            />
          </div>
          {/* Bucket */}
          <div className="bucket tool">
            <i className="fas fa-fill-drip" title="Background Color" />
            <input
              className="jscolor"
              defaultValue="ffffff"
              id="bucket-color"
            />
          </div>
          {/* Eraser */}
          <div className="tool">
            <i className="fas fa-eraser" id="eraser" title="Eraser" />
          </div>
          {/* Clear Canvas */}
          <div className="tool">
            <i className="fas fa-upload" id="clear-canvas" title="Clear" />
          </div>
          {/* Save Local Storage */}
          <div className="tool">
            <FontAwesomeIcon icon="fa-solid fa-download" />
          </div>
          {/* Load Local Storage */}
          <div className="tool">
            <i
              className="fas fa-upload"
              id="load-storage"
              title="Load Local Storage"
            />
          </div>
          {/* Clear Local Storage */}
          <div className="tool">
            <i
              className="far fa-save"
              id="clear-storage"
              title="Clear Local Storage"
            />
          </div>
          {/* Download Image */}
          <div className="tool">
            <a id="download">
              <i className="far fa-save" title="Save Image File" />
            </a>
          </div>
        </div>
        {/* Mobile Message */}
        <div className="mobile-message">
          <h2>Please use application on larger screen</h2>
        </div>
      </div>
      <Helmet>
        <script src="jscolor.js" type="text/javascript" />
        <script src="script.js" type="text/javascript" />
      </Helmet>
    </div>
  );
}

export default App;
