import React, {useEffect} from "react";
import "./App.css";

function App() {
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "./script.js";
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  }, []);
  return (
    <div>
      <div>
        <div className="top-bar">
          {/* Active Tool */}
          <div className="active-tool">
            <span id="active-tool" title="Active Tool">
              Brush
            </span>
          </div>
          {/* Brush */}
          <div className="brush tool">
            <i className="fas fa-brush" id="brush" title="Brush" />
            <input className="jscolor" defaultValue="a51dab" id="brush-color" />
            <span className="size" id="brush-size" title="Brush Size">
              10
            </span>
            <input
              type="range"
              min={1}
              max={50}
              defaultValue={10}
              className="slider"
              id="brush-slider"
            />
          </div>
          {/* Bucket */}
          <div className="bucket tool">
            <i className="fas fa-fill-drip" title="Background Color" />
            <input
              className="jscolor"
              defaultValue="ffffff"
              id="bucket-color"
            />
          </div>
          {/* Eraser */}
          <div className="tool">
            <i className="fas fa-eraser" id="eraser" title="Eraser" />
          </div>
          {/* Clear Canvas */}
          <div className="tool">
            <i className="fas fa-upload" id="clear-canvas" title="Clear" />
          </div>
          {/* Save Local Storage */}
          <div className="tool"></div>
          {/* Load Local Storage */}
          <div className="tool">
            <i
              className="fas fa-upload"
              id="load-storage"
              title="Load Local Storage"
            />
          </div>
          {/* Clear Local Storage */}
          <div className="tool">
            <i
              className="far fa-save"
              id="clear-storage"
              title="Clear Local Storage"
            />
          </div>
          {/* Download Image */}
          <div className="tool">
            <a id="download">
              <i className="far fa-save" title="Save Image File" />
            </a>
          </div>
        </div>
        {/* Mobile Message */}
        <div className="mobile-message">
          <h2>Please use application on larger screen</h2>
        </div>
      </div>

    </div>
  );
}

export default App;
